import React, { ChangeEvent, useState } from 'react';
import { Grid } from '@giphy/react-components';
import { GiphyFetch } from '@giphy/js-fetch-api';
import '../assets/css/inputSearch.css';

const gf = new GiphyFetch('0U6DW1ZMVMxiwu9Rk5tqnm4SgbfnpGmT');

const InputSearch = () => {

  const [search, setSearch] = useState("");

  const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
    setSearch(e.target.value);
  }

  // configure your fetch: fetch 10 gifs at a time as the user scrolls (offset is handled by the grid)
  const fetchGifs = (offset: number) => gf.search(search, { offset, limit: 10 });

  return (
    <>
      <h2>Search GIFS</h2>

      <input type="text" className="search-input" onChange={e => handleChange(e)} />

      <div className="search-box">
        <Grid width={800} columns={3} fetchGifs={fetchGifs} key={search} />
      </div>
    </>
  );
}

export default InputSearch;
